	public static final String GET_APPROVED_FEES = "select distinct fm.FeeCode,fm.FeeName,fsd.feeSettingCode,fsd.feeAmount from campus_fee_setupdetails fsd,campus_fee_setup fs,campus_fee_master fm where fsd.feeSettingCode=fs.feeSettingcode and fsd.feecode=fm.FeeCode and fs.ClassCode=? and fs.AccyearCode=? and fs.Termcode=? and fs.specialization=?";
	public static final String GET_ALL_FEES = "select distinct fm.FeeCode,fm.FeeName from campus_fee_master fm where fm.FeeCode  not in(select fsdd.FeeCode from campus_fee_setupdetails fsdd, campus_fee_setup fss1 where  fss1.feeSettingcode=fsdd.feeSettingCode  and  fss1.ClassCode=? and fss1.AccyearCode=? and fss1.Termcode=? and fss1.specialization=?)";
	public static final String INSERT_APPROVED_FEES_IN_FEE_SETUP = "insert into campus_fee_setup(feeSettingcode,ClassCode,AccyearCode,locationId,Termcode,specialization,createdby,createtime)values(?,?,?,?,?,?,?,now())";
	public static final String INSERT_APPROVED_FEES = "insert into  campus_fee_setupdetails(feeSettingCode,feecode,feeAmount)values(?,?,?);";
	public static final String DELETE_FEE_CODE = "delete from campus_fee_setupdetails where feeSettingCode=? and feecode=?";
	public static final String UPDATE_FEE_AMOUNT = "update campus_fee_setupdetails set feeAmount=? where feeSettingCode in (select feeSettingcode from campus_fee_setup where ClassCode=? and AccyearCode=? and Termcode=? and specialization=?) and feecode=?";
	public static final String DELETE_FEE_AMOUNT = "DELETE FROM campus_fee_setupdetails where feeSettingCode in (select feeSettingcode from campus_fee_setup where ClassCode=? and AccyearCode=? and Termcode=? and specialization=?) and feecode=?";
