	public static String getEventRegistrationList="	SELECT CASE WHEN CURDATE()<e.`endRegstation` THEN 'OPEN'  ELSE 'CLOSED' END regStatus,e.event_Id,e.`accyer_ID`,e.`location_ID`,l.Location_Name,a.acadamic_year,e.eventName,e.startsOn,e.endsOn FROM `campus_event_registration` e LEFT JOIN `campus_acadamicyear` a ON a.`acadamic_id`=e.`accyer_ID` LEFT JOIN campus_location l ON l.Location_Id=e.location_ID WHERE e.accyer_ID LIKE ? AND e.location_ID LIKE ? AND (l.Location_Name LIKE ? OR a.acadamic_year LIKE ? OR e.eventName LIKE ? OR e.startsOn LIKE ? OR e.endsOn LIKE ? OR e.regStatus LIKE ?)";
