	public static String GET_PARTICPANT_LIST_FROM_STUDENT_REGISTRATION="SELECT l.`Location_Name`,cs.*,csc.*, csc.student_rollno rollNo,csc.student_imgurl_var imgurl_var,cc.classdetails_name_var FROM campus_student cs LEFT JOIN campus_student_classdetails csc ON csc.student_id_int=cs.student_id_int LEFT JOIN campus_classdetail cc ON (cc.classdetail_id_int=csc.classdetail_id_int AND cc.locationId=csc.locationId) LEFT JOIN `campus_location` l ON l.`Location_Id`=cs.`locationId`  WHERE cs.student_admissionno_var=? AND csc.fms_acadamicyear_id_int=? ";
	public static String getEventRegiListIndividual ="SELECT DISTINCT cep.program_name,s.`participateType`,s.registrationNo,CONCAT(cs.`student_admissionno_var`,'_',cs.`student_fname_var`,' ',cs.`student_lname_var`) studentName, csc.`student_rollno`,cc.classdetails_name_var,l.`Location_Name`,csc.`student_imgurl_var`,  CASE WHEN  h.`housename`!='' THEN  h.`housename` ELSE  '-' END houseName,`registration_id`,s.`captain_admissionNo`,s.`registration_id`,s.created_time,csec.classsection_name_var FROM `campus_event_studentregistration` s LEFT JOIN `campus_student` cs ON cs.`student_admissionno_var`=s.`captain_admissionNo` LEFT JOIN campus_student_classdetails csc ON csc.student_id_int=cs.student_id_int LEFT JOIN campus_classdetail cc ON (cc.classdetail_id_int=csc.classdetail_id_int AND cc.locationId=csc.locationId) JOIN  campus_classsection csec ON csec.classsection_id_int=csc.classsection_id_int LEFT JOIN `campus_house_settings` h ON h.`house_id`=s.`house_id` LEFT JOIN `campus_location` l ON l.`Location_Id`=cs.`locationId` JOIN `campus_event_programcreation` cep ON cep.program_id=s.`program_id` WHERE s.`participateType`='individual' AND s.`event_id` like ? AND s.`category_id` LIKE ? AND s.`program_id` LIKE ? AND s.`house_id` LIKE ? and s.accId like ? AND csc.fms_acadamicyear_id_int=? ORDER BY `created_time` DESC";
