		public static final String GET_PARTICIPATED_STUDENTS = "SELECT l.`Location_Name`,cs.*,csc.*,cs.`student_id_int`,csc.student_rollno rollNo,csc.student_imgurl_var imgurl_var,cc.classdetails_name_var,css.`classsection_name_var`FROM campus_student cs LEFT JOIN campus_student_classdetails csc ON csc.student_id_int=cs.student_id_int LEFT JOIN campus_classdetail cc ON (cc.classdetail_id_int=csc.classdetail_id_int AND cc.locationId=csc.locationId) LEFT JOIN campus_classsection css ON (css.`classsection_id_int`=csc.`classsection_id_int` AND css.locationId=csc.locationId) LEFT JOIN `campus_location` l ON l.`Location_Id`=cs.`locationId` WHERE cs.student_admissionno_var=(SELECT `admission_no` FROM `campus_event_chestno_list` WHERE `chest_no`=?) AND csc.fms_acadamicyear_id_int=?";
