		public static final String  GET_PARTICPANT_CHESTNO_LIST_FROM_STUDENT_REGISTRATION="SELECT DISTINCT cecn.*,l.`Location_Name`,cs.*,csc.*,csc.student_imgurl_var imgurl_var FROM campus_student cs  JOIN campus_student_classdetails csc ON csc.student_id_int=cs.student_id_int JOIN campus_classdetail cc ON (cc.classdetail_id_int=csc.classdetail_id_int AND cc.locationId=csc.locationId) JOIN campus_location l ON l.`Location_Id`=cs.`locationId`  JOIN campus_event_chestno_list cecn ON cs.student_admissionno_var=cecn.admission_no JOIN campus_event_registration cer ON cer.accyer_ID=csc.fms_acadamicyear_id_int JOIN campus_event_category cec ON cec.eventId=cer.event_Id JOIN campus_event_programcreation cep ON (cep.event_id=cer.event_Id AND cep.category_id=cec.categoryId) WHERE cecn.admission_no =? AND cer.event_Id LIKE ? AND cec.categoryId LIKE ? AND cep.program_id LIKE ? ORDER BY cecn.chest_no * 1 ASC";
	    public static String GET_PARTICPANTLIST_FROM_EVENT_REGISTRATION="SELECT l.`Location_Name`,cs.*,csc.*, csc.student_rollno rollNo,csc.student_imgurl_var imgurl_var,cc.classdetails_name_var FROM campus_student cs LEFT JOIN campus_student_classdetails csc ON csc.student_id_int=cs.student_id_int LEFT JOIN campus_classdetail cc ON (cc.classdetail_id_int=csc.classdetail_id_int AND cc.locationId=csc.locationId) LEFT JOIN `campus_location` l ON l.`Location_Id`=cs.`locationId`  WHERE cs.student_admissionno_var=? AND cs.student_id_int=? ";
