		public static String getProgrammeDate="SELECT `Program_date` FROM `campus_event_program_scheduling` WHERE `Event_name`=? AND `Program_name`=? AND `Stage`=?";
	    public static String getProgramScheduledList_eventId="SELECT sc.`Makeup_report`,sc.`BackStage_report`,sc.Program_time,reg.eventName,reg.event_Id,pgrm.program_id,pgrm.program_name,pgrm.programDate,stg.stageId,stg.stageName,grn.greenroom_id,grn.greenroom_name FROM campus_event_registration reg    JOIN campus_event_programcreation pgrm ON pgrm.event_id=reg.event_Id   JOIN campus_event_stage stg ON stg.eventId=reg.event_Id JOIN campus_event_greenroom grn ON grn.event_id=reg.event_Id JOIN `campus_event_program_scheduling` sc ON sc.`Event_name`=reg.`event_Id` AND sc.`Program_name`=pgrm.`program_id` AND sc.`Stage`=stg.`stageId` AND sc.`Green_room`=grn.`greenroom_id` where reg.event_Id like ? ORDER BY pgrm.programDate,reg.eventName,pgrm.program_name ";
	    public static String getProgramScheduledList_progDate="SELECT sc.`Makeup_report`,sc.`BackStage_report`,sc.Program_time,reg.eventName,reg.event_Id,pgrm.program_id,pgrm.program_name,pgrm.programDate,stg.stageId,stg.stageName,grn.greenroom_id,grn.greenroom_name FROM campus_event_registration reg  JOIN campus_event_programcreation pgrm ON pgrm.event_id=reg.event_Id   JOIN campus_event_stage stg ON stg.eventId=reg.event_Id JOIN campus_event_greenroom grn ON grn.event_id=reg.event_Id  JOIN `campus_event_program_scheduling` sc ON sc.`Event_name`=reg.`event_Id` AND sc.`Program_name`=pgrm.`program_id` AND sc.`Stage`=stg.`stageId` AND sc.`Green_room`=grn.`greenroom_id` where reg.event_Id like ? and programDate like ? ORDER BY pgrm.programDate,reg.eventName,pgrm.program_name";
	    public static String getProgramNumberingList="SELECT  num.`programnumber`,stg.`stageName`,st.registration_id,prog.program_name,st.category_id,CONCAT(student_fname_var,' ',student_lname_var)AS student, prog.program_id,cat.categoryName,evt.eventName  FROM `campus_event_program_numbering` num   JOIN `campus_event_registration` evt  ON evt.event_Id=num.`event`  JOIN  campus_event_category cat ON cat.categoryId=num.`category` AND evt.event_Id = cat.eventId JOIN `campus_event_stage` stg ON stg.`stageId`=num.`stageId` AND evt.event_Id = stg.eventId  JOIN  campus_event_programcreation prog ON prog.program_id=num.`program` AND cat.categoryId = prog.category_id JOIN `campus_event_studentregistration`st ON  st.captain_admissionNo =num.`participants` AND st.event_id = num.`event` AND st.category_id = num.category AND st.`program_id`=num.`program`  JOIN  campus_student cs ON cs.`student_admissionno_var`=num.`participants` WHERE num.`event`=? AND num.`stageId`=? ORDER BY   num.programnumber";
	    public static String getStageAllocatedParticipants="SELECT  al.Event,CONCAT(cs.`student_fname_var`,'',cs.`student_lname_var`) AS student,ev.`eventName`,stg.`stageName`,prg.`program_name`,cat.`categoryName` FROM `campus_event_stage_allocation`  al  JOIN `campus_student` cs ON cs.`student_admissionno_var`=al.`ParticipantsName` JOIN `campus_event_registration` ev ON  ev.`event_Id`=al.`Event` JOIN `campus_event_stage` stg ON stg.`stageId`=al.`StageName` JOIN  `campus_event_programcreation` prg ON prg.`event_id`=al.Event JOIN `campus_event_category` cat ON cat.`eventId`=al.Event WHERE al.Event=? AND al.`StageName`=?";
	    public static String getListforCriteria="SELECT cat.`categoryId`,cat.`categoryName`,reg.eventName,reg.event_Id,pgrm.program_id,pgrm.program_name,pgrm.programDate FROM campus_event_registration reg  JOIN campus_event_programcreation pgrm ON pgrm.event_id=reg.event_Id JOIN `campus_event_category` cat ON cat.`categoryId`=pgrm.`category_id`  WHERE reg.event_Id LIKE ? AND pgrm.`category_id` LIKE ? AND pgrm.program_id LIKE ?  ORDER BY pgrm.programDate,reg.eventName,pgrm.program_name";
