	public static String showBlockListedData_staff="SELECT DISTINCT CASE WHEN ct.emailId =' ' THEN '----' ELSE ct.`emailId` END emailId, ct.registerId, CASE WHEN ct.`imagePath` IS NULL THEN 'images/profile_photo.png' ELSE ct.`imagePath` END imagePath, ls.`subscriberType`,ls.`staffId`,ls.`status`,ls.`paymentDate`, CONCAT(ct.`FirstName`,'',ct.`LastName`) AS staffName,ct.`Abbreviative_Id`,ct.`gender`, ct.`mobileNo`,ct.`presentAddress`,des.`designationName`,cd.`DEPT_NAME` FROM `campus_library_subscriber_details` ls LEFT JOIN `campus_teachers` ct ON ct.`TeacherID`=ls.`staffId` LEFT JOIN `campus_department` cd ON cd.`DEPT_ID` =ls.`department_Id` LEFT JOIN `campus_designation` des ON des.`DesignationCode`=ls.`designation_Id` WHERE ls.`subscriberType`='teacher' AND ls.subscriberId=? AND ls.`blockSubscriber`='no' AND ls.`loc_ID`=?";
	public static String showBlockListedData_student="SELECT CASE WHEN ccs.`student_imgurl_var` IS NULL THEN 'images/profile_photo.png' ELSE ccs.`student_imgurl_var` END student_imgurl_var,ls.`subscriberNumber`,ls.`paymentDate`,ls.`status`,CONCAT(cs.`student_fname_var`,' ',cs.`student_lname_var`)AS studentName,cs.`student_gender_var`,cs.`student_admissionno_var`, cc.`classdetails_name_var`,s.`classsection_name_var`,CASE WHEN `email`!='' THEN email WHEN email='' AND `student_mother_mailid`!='' THEN student_mother_mailid WHEN email='' AND student_mother_mailid='' AND student_gardian_mailid !=''  THEN student_gardian_mailid ELSE '----' END email,CASE WHEN p.mobileno IS NULL AND p.student_mothermobileno_var IS NOT NULL  THEN p.student_mothermobileno_var WHEN p.mobileno IS NULL AND p.student_mothermobileno_var IS NULL  AND p.student_gardian_mobileno IS NOT NULL THEN p.student_gardian_mobileno ELSE p.mobileno END  AS mobileNo,p.`presentAddress`,ccs.`student_rollno` FROM `campus_library_subscriber_details` ls LEFT JOIN `campus_student` cs ON cs.`student_id_int` =ls.`studentId` LEFT JOIN `campus_classdetail` cc ON cc.`classdetail_id_int`=ls.`Class_Id`LEFT JOIN `campus_classsection` s ON s.`classsection_id_int`=ls.`section_Id`LEFT JOIN `campus_parentchildrelation` c ON c.`stu_addmissionNo`=ls.`studentId` LEFT JOIN `campus_parents` p ON p.`ParentID`=c.`ParentID` LEFT JOIN `campus_student_classdetails` ccs ON ccs.`student_id_int`= ls.studentId  WHERE `subscriberType`='student' AND ls.subscriberId=? AND ls.`blockSubscriber`='no' AND ls.`loc_ID`=?";

	
