	public static final String GET_STUDENT_DETAIL_BY_SUBSCRIBER_DETAILS ="SELECT DISTINCT  lsd.subscriberId,lsd.loc_ID,cll.library_location_name,lsd.subscriberType,cl.Location_Id,CONCAT(st.student_lname_var,' ',st.student_fname_var)AS student,st.student_admissionno_var,lsd.subscriberType,lsd.subscriberNumber,st.`student_gender_var`,csc.student_rollno,cd.classdetails_name_var,cs.classsection_name_var,CASE WHEN mobileno IS NULL THEN student_mothermobileno_var ELSE mobileno END MobileNo, CASE WHEN email IS NULL THEN  student_mother_mailid ELSE email END EmailId,lsd.status,csc.student_imgurl_var,lsd.depositType,lsd.paymentDate,cp.`presentAddress`  FROM campus_student st JOIN campus_student_classdetails csc ON csc.student_id_int=st.student_id_int JOIN campus_classdetail cd ON csc.classdetail_id_int=cd.classdetail_id_int JOIN campus_classsection cs ON csc.classsection_id_int =cs.classsection_id_int JOIN campus_location cl ON cl.Location_Id=csc.locationId JOIN campus_library_subscriber_details lsd  ON lsd.`studentId`=st.`student_id_int` JOIN campus_parentchildrelation cpcr ON cpcr.`stu_addmissionNo`=lsd.`studentId` JOIN  `campus_parents` cp  ON cpcr.`parentid`=cp.`ParentID` JOIN  campus_library_location cll ON cll.library_loc_id=lsd.loc_ID WHERE lsd.accyear_ID like ? AND lsd.loc_ID like ? AND lsd.subscriberId like ?";
