	public static final String STUDENTS_OVERDUE_DETAILS ="SELECT DISTINCT lbi.book_issued_date,lse.Author,lsd.subscriberId,lsd.subscriberNumber,CONCAT(st.student_lname_var,' ',st.student_fname_var)AS student,cd.classdetails_name_var,cs.classsection_name_var,lse.Accession_number,lse.Book_title,lbi.to_date,lbr.book_returned_date FROM  campus_student st  JOIN campus_student_classdetails csc ON csc.student_id_int=st.student_id_int  JOIN campus_classdetail cd ON csc.classdetail_id_int=cd.classdetail_id_int  JOIN campus_classsection cs ON csc.classsection_id_int =cs.classsection_id_int  JOIN campus_library_subscriber_details lsd ON lsd.studentId=csc.student_id_int  JOIN campus_library_book_issue_details lbi ON lsd.subscriberId=lbi.sub_id JOIN campus_library_book_return_details lbr ON lbi.accession_no=lbr.accession_no JOIN campus_library_location cll ON cll.library_loc_id=lsd.loc_ID JOIN  campus_library_stock_entry lse ON  lse.Entry_id=lbi.accession_no WHERE (lbi.to_date < lbr.book_returned_date) AND csc.fms_acadamicyear_id_int like ? AND lsd.loc_ID like ? ";
