	public static final String LIBRARY_OVERDUE_STATEMENT_BY_STUDENT = "SELECT DISTINCT lsd.subscriberId,lsd.studentId,lsd.`subscriberType`,lbi.book_issued_date,lse.Author,lsd.subscriberId,lsd.subscriberNumber,lse.Accession_number,lse.Book_title,lbi.to_date,lbr.book_returned_date,CONCAT(student_fname_var,' ',student_lname_var)AS student,cd.classdetails_name_var,cs.classsection_name_var,lbr.status FROM campus_student st JOIN campus_student_classdetails csc ON st.student_id_int = csc.student_id_int JOIN campus_classdetail cd ON cd.classdetail_id_int = csc.classdetail_id_int AND cd.locationId = csc.locationId JOIN campus_classsection cs ON cs.classsection_id_int = csc.classsection_id_int AND csc.locationId = cs.locationId JOIN  campus_library_subscriber_details lsd ON lsd.studentId = csc.student_id_int JOIN campus_library_location cll ON cll.library_loc_id=lsd.loc_ID AND cll.loc_ID =? JOIN campus_library_book_return_details lbr ON lbr.sub_id = lsd.subscriberId LEFT JOIN campus_library_book_issue_details lbi ON lbi.issued_id = lbr.issue_id JOIN  campus_library_stock_entry lse ON lse.Entry_id=lbi.accession_no WHERE (lbi.to_date < CURDATE() AND lbi.STATUS = 'Issued') or (lbi.to_date < lbr.book_returned_date) AND lbr.user_type='Student' AND csc.classdetail_id_int LIKE ? AND csc.classsection_id_int LIKE ? ORDER BY length(lsd.subscriberNumber),lsd.subscriberNumber";
