	public static final String CLASS_TEACHER_MAPPED_LIST = "select cls.classdetail_id_int,cls.locationId,cls.classdetails_name_var,sec.classsection_id_int,sec.classsection_name_var ,(case when map.SectionCode=sec.classsection_id_int then (select concat(FirstName,' ',LastName)as teaname from campus_teachers where TeacherID=map.TeacherCode) else '-' end)teachername , (case when map.SectionCode=sec.classsection_id_int then (select TeacherID from  campus_teachers where TeacherID=map.TeacherCode) else '-' end)teacherid from campus_classdetail cls,campus_classsection sec left outer join  campus_classteacher map on  map.SectionCode=sec.classsection_id_int  where cls.classdetail_id_int=sec.classdetail_id_int order by LENGTH(cls.classdetail_id_int),cls.classdetail_id_int,sec.classsection_name_var";

