				public static final String GET_INACTIVE_STUDENTS_FEE_INFORMATION = "select st.student_admissionno_var,concat(st.student_fname_var,' ',st.student_lname_var)as studentname, concat(cls.classdetails_name_var,'-',sec.classsection_name_var)as ClassName,pt.FatherName,pt.mobileno,pt.student_mothername_var,pt.student_mothermobileno_var, pt.student_gaurdianname_var,pt.student_gardian_mobileno,cfc.totalamount,cfc.is_paid,cfc.paidDate,trm.termname from campus_parents pt join campus_parentchildrelation prc on prc.parentid = pt.ParentID join campus_student st on st.student_id_int = prc.stu_addmissionNo join campus_classdetail cls on cls.classdetail_id_int = st.classdetail_id_int join campus_classsection sec on sec.classsection_id_int = st.classsection_id_int join campus_fee_collection cfc on cfc.admissionNo = st.student_admissionno_var join campus_fee_termdetails trm on trm.termid = cfc.termcode where student_status_var = 'inactive' and st.fms_acadamicyear_id_int = ? order by st.student_admissionno_var asc " ;
