				public static final String GETSTUDENTSSTRENGTH = "select count(sc.student_id_int)as stucount,st.student_gender_var,sc.classdetail_id_int,sc.classsection_id_int from campus_student_classdetails sc join campus_student st on sc.student_id_int = st.student_id_int where sc.fms_acadamicyear_id_int like ? and sc.locationId like ? and sc.classdetail_id_int = ? and sc.classsection_id_int = ? group by st.student_gender_var order by st.student_gender_var";
				public static final String GETSTUDENTSNEWADMISSONLIST = "select stu.student_id_int,stu.student_admissionno_var,concat(stu.student_fname_var,' ',stu.student_lname_var)as studentName,ccd.classdetails_name_var,ccs.classsection_name_var,stu.student_doj_var from campus_student stu join campus_student_classdetails cscd on cscd.student_id_int=stu.student_id_int join campus_classdetail ccd on ccd.classdetail_id_int=cscd.classdetail_id_int join campus_classsection ccs on ccs.classsection_id_int=cscd.classsection_id_int where  stu.fms_acadamicyear_id_int=cscd.fms_acadamicyear_id_int and stu.fms_acadamicyear_id_int like ? and stu.locationId like ? and (stu.tempadmissionregno not in ('NULL') and stu.tempadmissionregno not in (' ')) and cscd.classdetail_id_int like ? and cscd.classsection_id_int like ? order by ccd.classdetails_name_var,ccs.classsection_name_var";
				public static final String GETSTUDENTSPROMOTIONLIST = "select distinct stu.student_id_int,case when cscd.student_rollno is null then '-' else cscd.student_rollno end student_rollno,concat(stu.student_fname_var,' ',stu.student_lname_var)as studentName,csp.studentpromotion_status from campus_student stu join campus_student_classdetails cscd on cscd.student_id_int=stu.student_id_int join campus_student_promotion csp on (csp.student_id_int=cscd.student_id_int and csp.studentpromotion_acadamicyearfrom_var=cscd.fms_acadamicyear_id_int) where csp.studentpromotion_acadamicyearfrom_var like ? and csp.studentpromotion_fromclass_var like ? and csp.studentpromotion_fromsection_var like ? and csp.studentpromotion_status='promoted' order by cscd.student_rollno";
				public static final String GETSTUDENTSLISTGENDERWISE = "select stu.student_admissionno_var,stu.student_gender_var,case when cscd.student_rollno is null then '-' else cscd.student_rollno end student_rollno,concat(stu.student_fname_var,' ',stu.student_lname_var) as studentName from campus_student stu left join campus_student_classdetails cscd on cscd.student_id_int=stu.student_id_int where cscd.fms_acadamicyear_id_int like ? and cscd.locationId like ? and cscd.classdetail_id_int like ? and cscd.classsection_id_int like ? and stu.student_gender_var like ? order by cscd.student_rollno";
				public static final String GETSUBJECTDETAILS = "select subjectID,subjectName from campus_subject where classid=? and locationId=?";
				public static final String GETACCESSION_NO ="SELECT Accession_number FROM campus_library_stock_entry";
				public static final String GET_EXAM = "SELECT `examcode` FROM `campus_exam_timetable` WHERE `accyear_id`=? AND `loc_id`=? AND `class_id`=? AND `section_id`=?";
				public static final String GET_DEP_EXAM = "SELECT COUNT(*) FROM`campus_exam_dependency` WHERE `Exam_code`=?";
				public static final String DEPENDENT_EXAM = "SELECT * FROM `campus_exam_dependency` WHERE `Exam_code`=?";
				public static final String EXAM_NAME = "SELECT ce.examname,SUM(stm.`scored_marks`) AS scored_marks,SUM(stm.`notebook_marks`) notebook_marks,SUM(stm.`subject_enrich_marks`) subject_enrich_marks,SUM(stm.`periodic_test`) periodic_test,SUM(stm.`total_marks`) AS totalmarks,SUM(stm.`max_periodic_marks`) AS max_periodic_marks,SUM(stm.`max_notebook_marks`) max_notebook_marks,SUM(stm.`max_subjenrich_marks`) max_subjenrich_marks FROM campus_examination ce LEFT JOIN campus_studentwise_mark_details stm ON stm.`exam_id`=ce.examid LEFT JOIN `campus_subject` sub ON sub.`subjectID`= stm.`sub_id` WHERE ce.examid=? AND stm.`stu_id`=? AND sub.`classid`=?";
				public static final String DEPENDENT_EXAM_SCORE = "SELECT cm.`ScoredMarks`,cm.`TotalMarks`,ce.`examname` FROM `campus_marks` cm JOIN `campus_examination` ce ON ce.`examid`=cm.`Exam`WHERE cm.`StudentId`=? AND cm.`Exam`=? AND cm.`Accyear`=? AND cm.`Classs`=? AND cm.`LocationId`=?";



				public static final String GET_FEE_COLLECTION_REPORT = "SELECT cft.termname,fc.paidDate,fc.chln_no,CONCAT(cd.classdetails_name_var, ' ',cs.classsection_name_var) AS class, CONCAT(st.student_fname_var,' ',st.student_lname_var)AS student ,pr.address,fc.paymentMode,fc.amount_paid FROM campus_student st JOIN campus_student_classdetails csc ON csc.student_id_int = st.student_id_int JOIN campus_parentchildrelation par ON par.stu_addmissionNo = csc.student_id_int JOIN  campus_parents pr ON pr.parentid = par.ParentID JOIN campus_classdetail cd ON cd.classdetail_id_int=csc.classdetail_id_int AND cd.locationId = csc.locationId JOIN campus_classsection cs ON cs.classsection_id_int=csc.classsection_id_int AND cs.locationId = csc.locationId JOIN campus_fee_collection fc ON fc.admissionNo = csc.student_id_int join campus_fee_termdetails cft on cft.termid=fc.termcode where csc.locationId like ? and csc.fms_acadamicyear_id_int like ? AND fc.termcode like ? order by fc.chln_no and cd.classdetails_name_var and cs.classsection_name_var and st.student_fname_var and st.student_lname_var";


				public static final String GET_FEE_COLLECTION_CLASS_REPORT = "SELECT cft.termname,fc.paidDate,fc.chln_no,CONCAT(cd.classdetails_name_var, ' ',cs.classsection_name_var) AS class, CONCAT(st.student_fname_var,' ',st.student_lname_var)AS student ,pr.address,fc.paymentMode,fc.amount_paid FROM campus_student st JOIN campus_student_classdetails csc ON csc.student_id_int = st.student_id_int JOIN campus_parentchildrelation par ON par.stu_addmissionNo = csc.student_id_int JOIN  campus_parents pr ON pr.parentid = par.ParentID JOIN campus_classdetail cd ON cd.classdetail_id_int=csc.classdetail_id_int AND cd.locationId = csc.locationId JOIN campus_classsection cs ON cs.classsection_id_int=csc.classsection_id_int AND cs.locationId = csc.locationId JOIN campus_fee_collection fc ON fc.admissionNo = csc.student_id_int join campus_fee_termdetails cft on cft.termid=fc.termcode where csc.locationId like ? and csc.fms_acadamicyear_id_int like ? and cd.classdetail_id_int like ? AND fc.termcode like ? order by fc.chln_no and cd.classdetails_name_var and cs.classsection_name_var and st.student_fname_var and st.student_lname_var";


				public static final String GET_FEE_COLLECTION_SECTION_REPORT = "SELECT cft.termname,fc.paidDate,fc.chln_no,CONCAT(cd.classdetails_name_var, ' ',cs.classsection_name_var) AS class, CONCAT(st.student_fname_var,' ',st.student_lname_var)AS student ,pr.address,fc.paymentMode,fc.amount_paid FROM campus_student st JOIN campus_student_classdetails csc ON csc.student_id_int = st.student_id_int JOIN campus_parentchildrelation par ON par.stu_addmissionNo = csc.student_id_int JOIN  campus_parents pr ON pr.parentid = par.ParentID JOIN campus_classdetail cd ON cd.classdetail_id_int=csc.classdetail_id_int AND cd.locationId = csc.locationId JOIN campus_classsection cs ON cs.classsection_id_int=csc.classsection_id_int AND cs.locationId = csc.locationId JOIN campus_fee_collection fc ON fc.admissionNo = csc.student_id_int join campus_fee_termdetails cft on cft.termid=fc.termcode where csc.locationId like ? and csc.fms_acadamicyear_id_int like ? and cd. classdetail_id_int like ? and cs.classsection_id_int like ? AND fc.termcode like ? order by fc.chln_no and cd.classdetails_name_var and cs.classsection_name_var and st.student_fname_var and st.student_lname_var";


				public static final String GET_FEE_COLLECTION_PAYMODE_REPORT = "SELECT cft.termname,fc.paidDate,fc.chln_no,CONCAT(cd.classdetails_name_var, ' ',cs.classsection_name_var) AS class, CONCAT(st.student_fname_var,' ',st.student_lname_var)AS student ,pr.address,fc.paymentMode,fc.amount_paid FROM  campus_fee_collection fc JOIN campus_student_classdetails csc ON fc.admissionNo = csc.student_id_int AND fc.accYear=csc.fms_acadamicyear_id_int JOIN campus_student st  ON fc.admissionNo = st.student_id_int JOIN campus_parentchildrelation par ON par.stu_addmissionNo = fc.admissionNo JOIN  campus_parents pr ON pr.parentid = par.ParentID JOIN campus_classdetail cd ON cd.classdetail_id_int=csc.classdetail_id_int AND cd.locationId = csc.locationId JOIN campus_classsection cs ON cs.classsection_id_int=csc.classsection_id_int AND cs.locationId = csc.locationId  JOIN campus_fee_termdetails cft ON cft.termid=fc.termcode where csc.locationId = ? and fc.accYear = ? and cd. classdetail_id_int like ? and cs.classsection_id_int like ? and fc.paymentMode LIKE ? AND  fc.termcode like ? order by fc.paidDate,fc.chln_no,length(cd.classdetail_id_int),cd.classdetail_id_int,cs.classsection_name_var,student";
            
