				public static final String GET_STUDENT_LIST_BY_SECTION = "SELECT DISTINCT stu.student_admissionno_var,acy.acadamic_year ,CASE WHEN stu.student_lname_var IS NULL THEN stu.student_fname_var ELSE CONCAT(stu.student_fname_var,' ',stu.student_lname_var)END studentname,ccd.classdetails_name_var,ccs.classsection_name_var,loc.`Location_Name`,stm.`classstream_name_var`,ce.`examname`,ce.`examcode`,cet.`examtypename`,sub.`subjectName`,ce.`startdate`,ce.`enddate` FROM campus_student stu JOIN campus_student_classdetails csc ON stu.student_id_int=csc.student_id_int LEFT JOIN campus_classdetail ccd ON csc.classdetail_id_int=ccd.classdetail_id_int LEFT JOIN `campus_classstream` stm ON stm.`classstream_id_int`=ccd.`classstream_id_int` LEFT JOIN campus_classsection ccs ON csc.classsection_id_int=ccs.classsection_id_int LEFT JOIN `campus_subject` sub ON sub.`classid`=ccd.classdetail_id_int LEFT JOIN campus_acadamicyear acy ON csc.fms_acadamicyear_id_int=acy.acadamic_id LEFT JOIN `campus_location` loc ON loc.`Location_Id`=csc.locationId,`campus_examination` ce LEFT JOIN `campus_examtype` cet ON ce.`examtype`=cet.`examtypeid` WHERE csc.locationId =? AND csc.fms_acadamicyear_id_int=? AND csc.classdetail_id_int=? AND csc.classsection_id_int=? AND ce.`examid`=? AND sub.`subjectID`=? ORDER BY studentname";
				public static final String GET_STUDENT_LIST_BY_SECTION1 = "SELECT DISTINCT stu.student_admissionno_var,acy.acadamic_year ,CASE WHEN stu.student_lname_var IS NULL THEN stu.student_fname_var ELSE CONCAT(stu.student_fname_var,' ',stu.student_lname_var)END studentname,ccd.classdetails_name_var,ccs.classsection_name_var,loc.`Location_Name`,stm.`classstream_name_var`,ce.`examname`,ce.`examcode`,cet.`examtypename`,ce.`startdate`,ce.`enddate` FROM campus_student stu JOIN campus_student_classdetails csc ON stu.student_id_int=csc.student_id_int LEFT JOIN campus_classdetail ccd ON csc.classdetail_id_int=ccd.classdetail_id_int LEFT JOIN `campus_classstream` stm ON stm.`classstream_id_int`=ccd.`classstream_id_int` LEFT JOIN campus_classsection ccs ON csc.classsection_id_int=ccs.classsection_id_int LEFT JOIN campus_acadamicyear acy ON csc.fms_acadamicyear_id_int=acy.acadamic_id LEFT JOIN `campus_location` loc ON loc.`Location_Id`=csc.locationId,`campus_examination` ce LEFT JOIN `campus_examtype` cet ON ce.`examtype`=cet.`examtypeid` WHERE csc.locationId =? AND csc.fms_acadamicyear_id_int=? AND csc.classdetail_id_int=? AND csc.classsection_id_int=? AND ce.`examid`=? ORDER BY studentname";	
